import { useState } from 'react'

const Filter = ({ v, f }) =>
  <form>
  <div>
    filter shown with
    <input
      value={v}
      onChange={f}
    />
  </div>
  </form>

const PersonForm = ({ v1, v2, f1, f2, f3 }) =>
  <form onSubmit={f1}>
    <div>
      name: 
      <input 
        value={v1} 
        onChange={f2}
      />
    </div>
    <div>
      number: 
      <input 
        value={v2}
        onChange={f3}
      />
    </div>
    <div>
      <button type="submit">add</button>
    </div>
  </form>

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456' },
    { name: 'Ada Lovelace', number: '39-44-5323523' },
    { name: 'Dan Abramov', number: '12-43-234345' },
    { name: 'Mary Poppendieck', number: '39-23-6423122' }
  ]) 
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [showAll, setShowAll] = useState(true)
  const [searchString, setSearchString] = useState('')

  const addContact = (event) => {
    event.preventDefault()
    const personObject = { name: newName, number: newNumber }
    if (persons.every(p => p.name != newName)) {
      setPersons(persons.concat(personObject))
    } else {
      alert(`${newName} is already added to phonebook`)
    }
    setNewName('')
    setNewNumber('')
  }

  const handleNameChange = (event) => {
    console.log(event.target.value)
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    console.log(event.target.value)
    setNewNumber(event.target.value)
  }

  const handleSearchStringChange = (event) => {
    console.log(event.target.value)
    setSearchString(event.target.value)
    setShowAll(false)
  }

  const contactsToShow = showAll
    ? persons
    : persons.filter(p => p.name.toLowerCase().includes(searchString))

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter v={searchString} f={handleSearchStringChange}/>
      <h2>Add a new</h2>
      <PersonForm 
        v1={newName}
        v2={newNumber}
        f1={addContact}
        f2={handleNameChange}
        f3={handleNumberChange}
      />
      <h2>Numbers</h2>
      {contactsToShow.map( (c, i) => <p key={i}>{c.name} {c.number}</p>)}
    </div>
  )

}

export default App
